import React from 'react'
import "./sidebar.css"
import Logo from '../../assets/logo.png'

// --first-color: hsl(353, 100%, 65%);
// --title-color: hsl(244, 24%, 26%);
// --text-color: hsl(244, 16%, 43%);
// --body-color: hsl(258, 60%, 98%);
// --container-color: #fff;
// --dark-first-color: hsl(353, 100%, 65%);
// --dark-title-color: hsl(244, 24%, 26%);
// --dark-text-color: hsl(244, 16%, 43%);
// --dark-body-color: hsl(258, 60%, 98%);
// --dark-container-color: #fff;

const Sidebar = () => {
    const toggleStyles = () => {
        const root = document.documentElement;
        if (root.style.getPropertyValue('--body-color') === 'hsl(258, 60%, 98%)') {
          // Change to your new color values
          root.style.setProperty('--body-color', 'hsl(240, 32%, 27%);');
          root.style.setProperty('--title-color', '/* your new color value */');
          console.log(root.style.getPropertyValue('--first-color'))
          console.log(root.style.getPropertyValue('--body-color'))
        } else {
          // Reset to the default values
          root.style.setProperty('--first-color', 'hsl(353, 100%, 65%)');
          root.style.setProperty('--title-color', 'hsl(244, 24%, 26%)');
        }
    };
  return (
    <aside className='aside'>
        <a href="#home" className="nav_logo">
            <img src={Logo} alt="" />
        </a>
        <nav className="nav">
            <div className="nav_menu">
                <ul className="nav_list">
                    <li className="nav_item">
                        <a href="#home" className="nav_link">
                        <i className="icon-home" ></i>
                        </a>
                    </li>
                    <li className="nav_item">
                        <a href="#about" className="nav_link">
                            <i className="icon-user-following"></i>
                        </a>
                    </li>
                    <li className="nav_item">
                        <a href="#portfolio" className="nav_link">
                            <i className="icon-layers"></i>
                        </a>
                    </li>
                    <li className="nav_item">
                        <a href="#contact" className="nav_link">
                            <i className="icon-bubble"></i>
                        </a>
                    </li>
                    <li className="nav_item"> 
                    <div  className="nav_link">
                            <i className="icon-bulb" onClick = {toggleStyles}></i>
                        </div>
                    </li>
                </ul>
            </div>
        </nav>
        <div className="nav_footer">
            <span className="copyright">
                &copy; 2023
            </span>
        </div>
    </aside>
  )
}

export default Sidebar
