import React, { useState } from "react";
import "./App.css";
import Sidebar from "./components/sidebar/Sidebar";
import Home from "./components/home/Home";
import About from "./components/about/About";
import Portfolio from "./components/portfolio/Portfolio";
import Contact from "./components/contact/Contact";

function App() {
  const [activeComponent, setActiveComponent] = useState("home");

  const handleComponentChange = (component) => {
    setActiveComponent(component);
  };

  return (
    <>
      <Sidebar handleComponentChange={handleComponentChange} />
      <main className="main">
        <Home className={`component ${activeComponent === "home" ? "active" : ""}`} />
        <About className={`component ${activeComponent === "about" ? "active" : ""}`} />
        <Portfolio className={`component ${activeComponent === "portfolio" ? "active" : ""}`} />
        <Contact className={`component ${activeComponent === "contact" ? "active" : ""}`} />
      </main>
    </>
  );
}

export default App;
