import React from 'react'
import './contact.css'

const Contact = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    subject: '',
    message: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post('/send-email', formData)
      .then((response) => {
        console.log(response.data);
        // Reset the form or show a success message to the user
      })
      .catch((error) => {
        console.error(error);
        // Handle the error or show an error message to the user
      });
  };
  return (
  <section className="contact container section" id="contact">
    <h2 className="section__title">Contact Me</h2>

    <div className="contact__container grid">
      <div className="contact__info">
        <h3 className="contact__title">Hi! Nice to meet you</h3>
          <p className="contact__details">
            Send me an email.
          </p>
      </div>

      <form action="" className="contact__form">
        <div className="contact__form-group">

          <div className="contact__form-div">
            <input type="text" className="contact__form-input" placeholder='Insert your name / company' />
          </div>

          <div className="contact__form-div">
            <input type="email" className="contact__form-input" placeholder='Insert your email' />
          </div>

          <div className="contact__form-div">
            <input type="text" className="contact__form-input" placeholder='Insert your subject' />
          </div>


        </div>
        <div className="contact__form-div message">
            <textarea name="" id="" cols="30" rows="10" className='contact__form-input'placeholder='Write Your Message'></textarea>
          </div>
        <button className="btn">Send Message</button>
      </form>
    </div>
  </section>
  )
}

export default Contact
