import React, { useState, useRef } from "react";
import "./App.css";
import Sidebar from "./components/sidebar/Sidebar";
import Home from "./components/home/Home";
import About from "./components/about/About";
import Portfolio from "./components/portfolio/Portfolio";
import Contact from "./components/contact/Contact";

function App() {
  const [activeComponent, setActiveComponent] = useState("home");
  const mainRef = useRef(null);

  const handleComponentChange = (component) => {
    setActiveComponent(component);
  };

  const handleScroll = (e) => {
    const scrollPosition = mainRef.current.scrollTop;
    const componentOffsets = {
      home: 0,
      about: 0, // Update this to the actual offset for the About component
      portfolio: 0, // Update this to the actual offset for the Portfolio component
      contact: 0, // Update this to the actual offset for the Contact component
    };

    // Determine the active component based on scroll position
    if (scrollPosition < componentOffsets.about) {
      setActiveComponent("home");
    } else if (scrollPosition < componentOffsets.portfolio) {
      setActiveComponent("about");
    } else if (scrollPosition < componentOffsets.contact) {
      setActiveComponent("portfolio");
    } else {
      setActiveComponent("contact");
    }
  };

  return (
    <>
      <Sidebar handleComponentChange={handleComponentChange} />
      <main
        className="main"
        ref={mainRef}
        onScroll={handleScroll}
        style={{
          scrollSnapType: "y mandatory",
          overflowY: "scroll",
        }}
      >
        <Home
          className={`component ${activeComponent === "home" ? "active" : ""}`}
        />
        <About
          className={`component ${activeComponent === "about" ? "active" : ""}`}
        />
        <Portfolio
          className={`component ${activeComponent === "portfolio" ? "active" : ""}`}
        />
        <Contact
          className={`component ${activeComponent === "contact" ? "active" : ""}`}
        />
      </main>
    </>
  );
}

export default App;
